import React, { useState } from 'react';
import './Basket.css';

function Basket({ cart, removeFromCart }) {
  const [showCheckoutModal, setShowCheckoutModal] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    surname: '',
    cardNumber: '',
    phoneNumber: '',
    address: '',
    email: '',
  });
  const [orderPlaced, setOrderPlaced] = useState(false);


  const calculateTotal = () => {
    return cart.reduce((total, product) => total + product.price, 0);
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handlePlaceOrder = () => {
    // Здесь может быть логика для обработки заказа (например, отправка данных на сервер)
    setOrderPlaced(true);
  };

  return (
    <div className="Basket">
      <h1>Корзина</h1>
      <ul>
        {cart.map(product => (
          <li key={product._id}>
            <img src={product.image} />
            <h3>{product.header}</h3>
            <p>{product.price} руб.</p>
            <button onClick={() => removeFromCart(product)}>Удалить</button>
          </li>
        ))}
      </ul>
      {cart.length > 0 && (
        <div className="total">
          Общая сумма: {calculateTotal()} руб.
          <button id='zakaz' onClick={() => setShowCheckoutModal(true)}>Оформить заказ</button>
        </div>
      )}
      {showCheckoutModal && (
        <div className="checkout-modal show">
          {!orderPlaced ? (
            <>
              <h2>Введите данные для оформления заказа</h2>
              <form>
                <label>
                  Имя: 
                  <input type="text" name="name" value={formData.name} onChange={handleInputChange} />
                </label>
                
                <button id='ofrm' type="button" onClick={handlePlaceOrder}>
                  Оформить заказ
                </button>
              </form>
            </>
          ) : (
            <p>Вы успешно оформили заказ!</p>
          )}
          <button id='cls' onClick={() => setShowCheckoutModal(false)}>X</button>
        </div>
      )}
    </div>
  );
}

export default Basket;
