import React, { useState, useEffect } from 'react';
import './Cabinet.css';
import axios from 'axios';

function Cabinet({ token }) {
    const [userData, setUserData] = useState({ email: '' });

    useEffect(() => {
        // Вызывайте ваш API для получения данных пользователя при монтировании компонента
        // Замените 'http://127.0.0.1:9001/user/getUserData' на реальный эндпоинт
        axios.get('http://127.0.0.1:9001/user/getUserData', {
            headers: {
                Authorization: `Bearer ${token}`
            }
        })
        .then(response => {
            setUserData({ email: response.data.email });
        })
        .catch(error => {
            console.error('Error fetching user data:', error);
        });
    }, [token]);

    function changeEmail() {
        const email = document.getElementById('email').value;

        const validEmail = email.match(/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g);

        if (!validEmail) {
            document.getElementById('emailMessage').innerText = "Вы ввели данные неправильно!";
            return;
        }

        const data = {
            token: token,
            email: email
        };

        console.log(data);

        const api = 'http://127.0.0.1:9001/user/changeEmail';

        fetch(api, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
        .then((result) => result.json())
        .then((result) => {
            console.log(result);
            document.getElementById('emailMessage').innerText = result.message;
        });
    }

    function changePass() {
        const pass = document.getElementById('pass').value;

        if (pass.length === 0) {
            document.getElementById('passMessage').innerText = "Вы ввели данные неправильно!";
            return;
        }

        const data = {
            token: token,
            password: pass
        };

        console.log(data);

        const api = 'http://127.0.0.1:9001/user/changePassword';

        fetch(api, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
        .then((result) => result.json())
        .then((result) => {
            console.log(result);
            document.getElementById('passMessage').innerText = result.message;
        });
    }

    return (
        <div className="Cabinet">
            <h1>Личный кабинет</h1>
            <p id='showEmail'>Ваш E-Mail: {userData.email} </p>
            <input id='email' placeholder='E-Mail' type='email' />
            <button id='sendEmail' onClick={changeEmail}>Сменить E-Mail</button>
            <p id='emailMessage'></p>
            <input id='pass' placeholder='Пароль' type='password' />
            <button id='sendPass' onClick={changePass}>Сменить пароль</button>
            <p id='passMessage'></p>
        </div>
    );
}

export default Cabinet;
