import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './components/Header';
import Footer from './components/Footer';
import ModalBox from './components/ModalBox';
import Login from './components/Login';
import Registration from './components/Registration';
import Basket from './views/Basket';
import Main from './views/Main';
import Cabin from './views/Cabin';

function App() {
  const [page, setPage] = useState('Main');
  const [modalBox, setModalBox] = useState('none');
  const [cart, setCart] = useState([]);
  const [user, setUser] = useState({});
  const [token, setToken] = useState('');

  const modalBoxes = {
    none: null,
    Login: <ModalBox setModalBox={setModalBox}><Login setToken={setToken} setUser={setUser} /></ModalBox>,
    Registration: <ModalBox setModalBox={setModalBox}><Registration /></ModalBox>
  };

  useEffect(() => {
    // Получение данных пользователя и токена из сессии или localStorage
    const userFromSession = JSON.parse(sessionStorage.getItem('user'));
    const tokenFromSession = sessionStorage.getItem('token');

    if (userFromSession && tokenFromSession) {
      setUser(userFromSession);
      setToken(tokenFromSession);
    } else {
      // Перенаправление на страницу входа, если данные отсутствуют
      // Реализуйте логику перенаправления по своему усмотрению
    }
  }, []);

  const updateUser = async (newUserData) => {
    try {
      const response = await fetch('http://localhost:3001/changeUserData', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': token,
        },
        body: JSON.stringify(newUserData),
      });

      if (response.ok) {
        // Обновление данных пользователя в сессии или localStorage
        sessionStorage.setItem('user', JSON.stringify({ ...user, ...newUserData }));
      } else {
        throw new Error('Failed to update user data');
      }
    } catch (error) {
      throw new Error('Failed to update user data');
    }
  };

  return (
    <div className="App">
      <Header setPage={setPage} setModalBox={setModalBox} />
      {modalBoxes[modalBox]}
      {page === 'Main' && <Main addToCart={addToCart} />}
      {page === 'Basket' && <Basket cart={cart} removeFromCart={removeFromCart} />}
      {page === 'Cabin' && <Cabin user={user} updateUser={updateUser} />}
      <Footer />
    </div>
  );
}


export default App;
