import React, { useState, useEffect } from 'react';
import './Cabinet.css';

function Cabinet({ token }) {
    const [userData, setUserData] = useState(null);

    useEffect(() => {
      const fetchUserData = async () => {
        try {
          const response = await fetch('./Гser', {
            method: 'GET',
            headers: {
              'Content-Type': 'application/json',
              // 'Authorization': `Bearer ${yourToken}`, // Добавьте заголовок с токеном, если необходимо
            },
          });
  
          const user = await response.json();
          setUserData(user);
        } catch (error) {
          console.error(error);
        }
      };
  
      fetchUserData();
    }, []);

    function changeEmail() {
        const email = document.getElementById('email').value

        const validEmail = email.match(/^[\w-.]+@([\w-]+\.)+[\w-]{2,4}$/g)

        if (!validEmail) {
            document.getElementById('emailMessage').innerText = "Вы ввели данные неправильно!"
            return
        }

        const data = {
            token: token,
            email: email
        }

        console.log(data)

        const api = 'http://127.0.0.1:9001/user/changeEmail'

        fetch(api, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
            .then((result) => result.json())
            .then((result) => {
                console.log(result)
                document.getElementById('emailMessage').innerText = result.message
            })
    }

    function changePass() {
        const pass = document.getElementById('pass').value

        if (pass.length === 0) {
            document.getElementById('emailMessage').innerText = "Вы ввели данные неправильно!"
            return
        }

        const data = {
            token: token,
            password: pass
        }

        console.log(data)

        const api = 'http://127.0.0.1:9001/user/changePassword'

        fetch(api, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
            .then((result) => result.json())
            .then((result) => {
                console.log(result)
                document.getElementById('passMessage').innerText = result.message
            })
    }


    return (
        <div className="Cabinet">
            <h1>Личный кабинет</h1>
            <p id='showEmail'>Ваш E-Mail: {userData.email} </p>
            <input id='email' placeholder='E-Mail' type='email' />
            <button id='sendEmail' onClick={changeEmail}>Сменить E-Mail</button>
            <p id='emailMessage'></p>
            <input id='pass' placeholder='Пароль' type='password' />
            <button id='sendPass' onClick={changePass}>Сменить пароль</button>
            <p id='passMessage'></p>
        </div>
    );
}

export default Cabinet;
